type NextLinkProps = Omit<LinkProps, 'as' | 'href'>;

type LinkComponentProps = PropsWithChildren<NextLinkProps> & {
    href?: LinkProps['href'];
};

function LinkComponent({
    href,
    replace,
    scroll,
    shallow,
    passHref,
    prefetch,
    locale,
    ...props
